CREATE TABLE IF NOT EXISTS `dtb_product` (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY,
  `product_category_id` bigint(20) UNSIGNED DEFAULT NULL,
  `pretty_url` varchar(500) NOT NULL,
  `code` varchar(50) DEFAULT NULL,
  `name` varchar(200) DEFAULT NULL,
  `description` mediumtext,
  `image_url` varchar(500) DEFAULT NULL,
  `image_pretty_name` varchar(200) DEFAULT NULL,
  `image_url_hot` varchar(500) DEFAULT NULL,
  `image_pretty_name_hot` varchar(200) DEFAULT NULL,
  `file_url` varchar(500) DEFAULT NULL,
  `filename` varchar(200) DEFAULT NULL,
  `file_url_msds` varchar(500) DEFAULT NULL,
  `filename_msds` varchar(200) DEFAULT NULL,
  `usability_feature` mediumtext,
  `technical_data` mediumtext,
  `surface_prep` mediumtext,
  `how_to_use` mediumtext,
  `cleaning_tools` mediumtext,
  `how_to_store` mediumtext,
  `safety_info` mediumtext,
  `packaging` mediumtext,
  `additional_information` mediumtext,
  `is_show` tinyint(1) DEFAULT '1' COMMENT '0: hide, 1: show',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '0: delete, 1 : not delete',
  `show_in_filter` tinyint(1) NOT NULL DEFAULT '10' COMMENT '0: no, 1: yes',
  `is_hot_item` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0: no, 1: yes',
  `ordering` bigint(20) UNSIGNED DEFAULT NULL,
  `created_date` datetime DEFAULT NULL,
  `updated_date` datetime DEFAULT NULL,
  `deleted_date` datetime DEFAULT NULL,
  `version` bigint(20) NOT NULL,
  `meta_keys` text,
  `meta_desc` text
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=72;


